# -*- coding: utf-8 -*-

__capability__ = 6

def arp_flush(cmd, *args, **argv):
    """
	delete all solidified arp entry
    """

    context = argv["context"]

    from _arp_deal import arp_del_all as del_all_arp

    exit_state = del_all_arp()

    if exit_state == True:
        return
    else:
        context.write("%s" % exit_state)
