#!/usr/bin/python
# -*- coding: utf-8 -*-

__alias__ = ["q", "exit()","quit", "Q"]
__capability__ = 7

import consh.ansi as ansi

def exit(cmd, *args, **argv):
    """
...     
_n      exit
_f      exit [n]
        Exit the shell.

        Exits the shell with a status of N.  If N is omitted, the exit status 
        is that of the last command executed.

    """

    context = argv["context"]
    context.write(ansi.RED + "Are you sure that you want to exit shell?" + ansi.BOLD + " [y/N]" + ansi.RESET, ban_newline = True)
    if context.read().lower() == "y":
        context.exit()

