# -*- coding: utf-8 -*-

__alias__ = ["h", "H", "?"]
__capability__ = 6

def ping_help(cmd, *args, **argv):
    """

    """
    context = argv["context"]
    cmds = cmd.split()
    cmds.pop()
    cmd = "_".join(cmds)
    
    helpinfo = context.get_doc(cmd, "format")

    if helpinfo != "":
        if helpinfo != None:
            context.write("%s" % helpinfo)
        
        else:
            # #
            pass
            return
    else:
        # #
        pass
        return

