# -*- coding: utf-8 -*-

__capability__ = 6

def route_help(cmd, *args, **argv):
    """
 
    """
    context = argv["context"]

    cmds = cmd.split()
    cmds.pop()
    cmd = "_".join(cmds)

    helpinfo = context.get_doc(cmd)
    if helpinfo != "":
        if helpinfo != None:
            context.write("%s" % helpinfo["format"])
            context.write("%s" % helpinfo["description"])
            return
        else:
            # #
            pass
            return
    else:
        # #
        pass
        return 
