# -*- coding: utf-8 -*-

__capability__ = 6

def sysinfo(cmd, *args, **argv):
    """
...     
_n      Information about system
_f      sysinfo [help] [sys] [interface]
        Options:
          help          get help about sysinfo
          sys           get infomations about system
          interface     get informatiuon about interface
        
    """
    
    context = argv["context"]

    if len(args):
        cmd_next = args[0]
        args = list(args)
        args.pop(0)
        context.func_next(cmd, cmd_next, args)
        return 
    else:
        context.func_next(cmd, "help","")
