# -*- coding: utf-8 -*-

__capability__ = 6

def sysinfo_help(cmd, *args, **argv):
    """
    """

    context = argv["context"]

    cmds = cmd.split()
    cmds.pop()
    cmd = "_".join(cmds)

    helpinfo = context.get_doc(cmd)

    if helpinfo != "":
        if helpinfo != None:
            context.write("Usage: %s" % helpinfo["format"].strip())

        else:
            # #
            pass

    else:

        # #
        pass

