# -*- coding: utf-8 -*-

__capability__ = 6

def telnet(cmd, *args, **argv):
    """
...     
_n      Telnet
_f      telnet [-l user] [-a] host-name [port]
        
    """

    context = argv["context"]
    
    cmds = cmd
    if len(args):
        for arg in args:
            cmds = cmds + " " + arg
    import os 
    os.system("%s"% cmds)   

